import React, { useState, useEffect } from 'react';
import { useSearchParams, useNavigate } from 'react-router-dom';
import SearchBar from './SearchBar';
import ProductCard from './ProductCard';
import FilterPanel from './FilterPanel';
import axios from 'axios';

const SearchPage = () => {
  const [searchParams, setSearchParams] = useSearchParams();
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [totalResults, setTotalResults] = useState(0);
  const navigate = useNavigate();

  const searchTerm = searchParams.get('q') || '';
  const category = searchParams.get('category') || '';
  const filters = {
    color: searchParams.get('color') || '',
    brand: searchParams.get('brand') || '',
    occasion: searchParams.get('occasion') || '',
    minPrice: searchParams.get('minPrice') || '',
    maxPrice: searchParams.get('maxPrice') || ''
  };

  useEffect(() => {
    if (searchTerm || category || Object.values(filters).some(f => f)) {
      performSearch();
    }
  }, [searchParams]);

  const performSearch = async () => {
    try {
      setLoading(true);
      setError(null);

      const params = {
        q: searchTerm,
        category,
        ...filters
      };

      // Remove empty parameters
      Object.keys(params).forEach(key => {
        if (!params[key]) delete params[key];
      });

      const response = await axios.get('/api/search', { params });
      setProducts(response.data.products || []);
      setTotalResults(response.data.total || 0);
    } catch (err) {
      console.error('Search error:', err);
      setError(err.response?.data?.message || 'Search failed. Please try again.');
      setProducts([]);
      setTotalResults(0);
    } finally {
      setLoading(false);
    }
  };

  const handleSearch = (newSearchTerm, newFilters) => {
    const params = new URLSearchParams({
      q: newSearchTerm,
      ...newFilters
    });

    // Remove empty parameters
    for (let [key, value] of params.entries()) {
      if (!value) params.delete(key);
    }

    setSearchParams(params);
  };

  const handleFilterChange = (newFilters) => {
    const params = new URLSearchParams({
      q: searchTerm,
      category,
      ...newFilters
    });

    // Remove empty parameters
    for (let [key, value] of params.entries()) {
      if (!value) params.delete(key);
    }

    setSearchParams(params);
  };

  const handleProductClick = (productId) => {
    navigate(`/product/${productId}`);
  };

  return (
    <div className="search-page">
      <div className="container">
        {/* Search Header */}
        <div className="search-header">
          <SearchBar 
            onSearch={handleSearch}
            initialValue={searchTerm}
            placeholder="Search by item, color, brand, or occasion..."
          />
        </div>

        {/* Results Header */}
        {(searchTerm || category) && (
          <div className="results-header">
            <h2 className="results-title">
              {loading ? 'Searching...' : 
               error ? 'Search Error' :
               totalResults === 0 ? 'No Results Found' :
               `${totalResults} Result${totalResults !== 1 ? 's' : ''} Found`}
            </h2>

            {searchTerm && (
              <p className="search-query">
                Search results for: <span className="accent-text">"{searchTerm}"</span>
              </p>
            )}

            {category && (
              <p className="category-filter">
                Category: <span className="accent-text">{category}</span>
              </p>
            )}
          </div>
        )}

        <div className="search-content">
          {/* Filter Sidebar */}
          <aside className="filters-sidebar">
            <FilterPanel
              filters={filters}
              onFilterChange={handleFilterChange}
            />
          </aside>

          {/* Results Section */}
          <section className="results-section">
            {loading ? (
              <div className="loading-state">
                <div className="loading-spinner"></div>
                <p>Searching for products...</p>
              </div>
            ) : error ? (
              <div className="error-state">
                <i data-feather="alert-circle"></i>
                <h3>Search Error</h3>
                <p>{error}</p>
                <button 
                  className="btn-primary" 
                  onClick={performSearch}
                >
                  Try Again
                </button>
              </div>
            ) : products.length === 0 ? (
              <div className="empty-state">
                <i data-feather="search"></i>
                <h3>No Products Found</h3>
                <p>
                  {searchTerm || Object.values(filters).some(f => f) ? 
                    'Try adjusting your search terms or filters' :
                    'Use the search bar above to find products'
                  }
                </p>
              </div>
            ) : (
              <div className="products-grid">
                {products.map(product => (
                  <ProductCard
                    key={product.id}
                    product={product}
                    onClick={() => handleProductClick(product.id)}
                  />
                ))}
              </div>
            )}
          </section>
        </div>
      </div>
    </div>
  );
};

export default SearchPage;

src/components/FilterPanel.js.content:
import React, { useState } from 'react';

const FilterPanel = ({ 
  filters = {}, 
  onFilterChange,
  showPriceFilter = true,
  showBrandFilter = true,
  showOccasionFilter = true,
  showColorFilter = true,
  showCategoryFilter = false
}) => {
  const [localFilters, setLocalFilters] = useState(filters);
  const [isCollapsed, setIsCollapsed] = useState(false);

  const handleFilterChange = (filterKey, value) => {
    const newFilters = { ...localFilters, [filterKey]: value };
    setLocalFilters(newFilters);
    onFilterChange(newFilters);
  };

  const clearAllFilters = () => {
    const clearedFilters = Object.keys(localFilters).reduce((acc, key) => {
      acc[key] = '';
      return acc;
    }, {});
    setLocalFilters(clearedFilters);
    onFilterChange(clearedFilters);
  };

  const hasActiveFilters = Object.values(localFilters).some(filter => filter);

  const colorOptions = [
    { value: 'black', label: 'Black', hex: '#000000' },
    { value: 'white', label: 'White', hex: '#FFFFFF' },
    { value: 'gray', label: 'Gray', hex: '#808080' },
    { value: 'blue', label: 'Blue', hex: '#0066CC' },
    { value: 'red', label: 'Red', hex: '#CC0000' },
    { value: 'green', label: 'Green', hex: '#00CC00' },
    { value: 'yellow', label: 'Yellow', hex: '#FFCC00' },
    { value: 'brown', label: 'Brown', hex: '#8B4513' },
    { value: 'pink', label: 'Pink', hex: '#FF69B4' },
    { value: 'purple', label: 'Purple', hex: '#800080' },
    { value: 'orange', label: 'Orange', hex: '#FF8800' },
    { value: 'navy', label: 'Navy', hex: '#000080' }
  ];

  const occasionOptions = [
    'casual', 'formal', 'business', 'party', 'date', 'workout', 
    'travel', 'beach', 'wedding', 'interview', 'brunch', 'night-out'
  ];

  const categoryOptions = [
    'topwear', 'bottomwear', 'footwear', 'accessories'
  ];

  return (
    <div className="filter-panel">
      <div className="filter-header">
        <h3>Filters</h3>
        <div className="filter-header-actions">
          {hasActiveFilters && (
            <button 
              className="clear-all"
              onClick={clearAllFilters}
              title="Clear all filters"
            >
              Clear All
            </button>
          )}
          <button 
            className="collapse-toggle"
            onClick={() => setIsCollapsed(!isCollapsed)}
            title={isCollapsed ? 'Expand filters' : 'Collapse filters'}
          >
            <i data-feather={isCollapsed ? 'chevron-down' : 'chevron-up'}></i>
          </button>
        </div>
      </div>

      <div className={`filter-content ${isCollapsed ? 'collapsed' : ''}`}>
        {/* Price Range Filter */}
        {showPriceFilter && (
          <div className="filter-group">
            <label className="filter-label">Price Range</label>
            <div className="price-range">
              <input
                type="number"
                placeholder="Min"
                value={localFilters.minPrice || ''}
                onChange={(e) => handleFilterChange('minPrice', e.target.value)}
                className="price-input"
                min="0"
                step="0.01"
              />
              <span className="price-separator">to</span>
              <input
                type="number"
                placeholder="Max"
                value={localFilters.maxPrice || ''}
                onChange={(e) => handleFilterChange('maxPrice', e.target.value)}
                className="price-input"
                min="0"
                step="0.01"
              />
            </div>
          </div>
        )}

        {/* Brand Filter */}
        {showBrandFilter && (
          <div className="filter-group">
            <label className="filter-label">Brand</label>
            <input
              type="text"
              placeholder="Enter brand name"
              value={localFilters.brand || ''}
              onChange={(e) => handleFilterChange('brand', e.target.value)}
              className="filter-input"
            />
          </div>
        )}

        {/* Color Filter */}
        {showColorFilter && (
          <div className="filter-group">
            <label className="filter-label">Color</label>
            <div className="color-grid">
              {colorOptions.map(color => (
                <label key={color.value} className="color-option">
                  <input
                    type="radio"
                    name="color"
                    value={color.value}
                    checked={localFilters.color === color.value}
                    onChange={(e) => handleFilterChange('color', e.target.value)}
                  />
                  <div className="color-swatch" style={{ backgroundColor: color.hex }}>
                    {localFilters.color === color.value && (
                      <i data-feather="check" className="color-check"></i>
                    )}
                  </div>
                  <span className="color-label">{color.label}</span>
                </label>
              ))}
              <label className="color-option">
                <input
                  type="radio"
                  name="color"
                  value=""
                  checked={!localFilters.color}
                  onChange={(e) => handleFilterChange('color', '')}
                />
                <div className="color-swatch any-color">
                  <span>Any</span>
                </div>
              </label>
            </div>
          </div>
        )}

        {/* Occasion Filter */}
        {showOccasionFilter && (
          <div className="filter-group">
            <label className="filter-label">Occasion</label>
            <select
              value={localFilters.occasion || ''}
              onChange={(e) => handleFilterChange('occasion', e.target.value)}
              className="filter-select"
            >
              <option value="">Any Occasion</option>
              {occasionOptions.map(occasion => (
                <option key={occasion} value={occasion}>
                  {occasion.charAt(0).toUpperCase() + occasion.slice(1).replace('-', ' ')}
                </option>
              ))}
            </select>
          </div>
        )}

        {/* Category Filter */}
        {showCategoryFilter && (
          <div className="filter-group">
            <label className="filter-label">Category</label>
            <select
              value={localFilters.category || ''}
              onChange={(e) => handleFilterChange('category', e.target.value)}
              className="filter-select"
            >
              <option value="">All Categories</option>
              {categoryOptions.map(category => (
                <option key={category} value={category}>
                  {category.charAt(0).toUpperCase() + category.slice(1)}
                </option>
              ))}
            </select>
          </div>
        )}

        {/* Active Filters Summary */}
        {hasActiveFilters && (
          <div className="active-filters">
            <h4>Active Filters:</h4>
            <div className="filter-tags">
              {Object.entries(localFilters).map(([key, value]) => {
                if (!value) return null;

                let displayValue = value;
                if (key === 'minPrice' || key === 'maxPrice') {
                  displayValue = `$${value}`;
                }

                return (
                  <span key={key} className="filter-tag">
                    {key.replace(/([A-Z])/g, ' $1').toLowerCase()}: {displayValue}
                    <button
                      onClick={() => handleFilterChange(key, '')}
                      className="remove-filter"
                      title="Remove filter"
                    >
                      <i data-feather="x"></i>
                    </button>
                  </span>
                );
              })}
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default FilterPanel;

src/components/CategoryCard.js.content:
import React from 'react';

const CategoryCard = ({ category, onClick }) => {
  const handleClick = () => {
    if (onClick) {
      onClick(category.id);
    }
  };

  return (
    <div className="category-card" onClick={handleClick}>
      <div className="category-icon">
        <i data-feather={category.icon || 'package'}></i>
      </div>

      <div className="category-content">
        <h3 className="category-name">{category.name}</h3>
        {category.description && (
          <p className="category-description">{category.description}</p>
        )}
      </div>

      <div className="category-arrow">
        <i data-feather="arrow-right"></i>
      </div>
    </div>
  );
};

export default CategoryCard;

src/components/WardrobePage.js.content:
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import WardrobeItemCard from './WardrobeItemCard';
import axios from 'axios';

const WardrobePage = () => {
  const navigate = useNavigate();
  const [items, setItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showAddModal, setShowAddModal] = useState(false);
  const [editingItem, setEditingItem] = useState(null);
  const [filterCategory, setFilterCategory] = useState('');
  const [formData, setFormData] = useState({
    name: '',
    category: 'topwear',
    brand: '',
    color: '',
    colorHex: '#000000',
    size: '',
    material: '',
    tags: '',
    imageUrl: '',
    description: ''
  });

  const categories = ['topwear', 'bottomwear', 'footwear'];
  const userId = 'user1';

  useEffect(() => {
    loadWardrobeItems();
  }, [filterCategory]);

  const loadWardrobeItems = async () => {
    try {
      setLoading(true);
      const params = filterCategory ? `?category=${filterCategory}` : '';
      const response = await axios.get(`/api/users/${userId}/wardrobe${params}`);
      setItems(response.data.items);
    } catch (error) {
      console.error('Error loading wardrobe:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleAddItem = async (e) => {
    e.preventDefault();
    try {
      await axios.post(`/api/users/${userId}/wardrobe`, formData);
      setShowAddModal(false);
      resetForm();
      loadWardrobeItems();
    } catch (error) {
      console.error('Error adding item:', error);
      alert('Failed to add item to wardrobe');
    }
  };

  const handleUpdateItem = async (e) => {
    e.preventDefault();
    try {
      await axios.patch(`/api/users/${userId}/wardrobe/${editingItem.id}`, formData);
      setEditingItem(null);
      resetForm();
      loadWardrobeItems();
    } catch (error) {
      console.error('Error updating item:', error);
      alert('Failed to update item');
    }
  };

  const handleDeleteItem = async (itemId) => {
    try {
      await axios.delete(`/api/users/${userId}/wardrobe/${itemId}`);
      loadWardrobeItems();
    } catch (error) {
      console.error('Error deleting item:', error);
      alert('Failed to delete item');
    }
  };

  const handleEditItem = (item) => {
    setEditingItem(item);
    setFormData({
      name: item.name,
      category: item.category,
      brand: item.brand || '',
      color: item.color || '',
      colorHex: item.colorHex || '#000000',
      size: item.size || '',
      material: item.material || '',
      tags: item.tags || '',
      imageUrl: item.imageUrl || '',
      description: item.description || ''
    });
  };

  const handleGetOutfits = (itemId) => {
    navigate(`/wardrobe/recommendations/${itemId}`);
  };

  const resetForm = () => {
    setFormData({
      name: '',
      category: 'topwear',
      brand: '',
      color: '',
      colorHex: '#000000',
      size: '',
      material: '',
      tags: '',
      imageUrl: '',
      description: ''
    });
  };

  const handleCloseModal = () => {
    setShowAddModal(false);
    setEditingItem(null);
    resetForm();
  };

  const filteredItems = items;

  return (
    <div className="wardrobe-page">
      <div className="container">
        <div className="page-header">
          <div>
            <h1>My Wardrobe</h1>
            <p className="page-subtitle">Manage your personal clothing collection</p>
          </div>
          <button 
            className="btn-primary"
            onClick={() => setShowAddModal(true)}
          >
            <i data-feather="plus"></i>
            Add Item
          </button>
        </div>

        <div className="wardrobe-filters">
          <div className="filter-group">
            <label>Category:</label>
            <select 
              value={filterCategory} 
              onChange={(e) => setFilterCategory(e.target.value)}
              className="filter-select"
            >
              <option value="">All Categories</option>
              {categories.map(cat => (
                <option key={cat} value={cat}>
                  {cat.charAt(0).toUpperCase() + cat.slice(1)}
                </option>
              ))}
            </select>
          </div>
          <div className="items-count">
            {filteredItems.length} {filteredItems.length === 1 ? 'item' : 'items'}
          </div>
        </div>

        {loading ? (
          <div className="loading-state">Loading your wardrobe...</div>
        ) : filteredItems.length === 0 ? (
          <div className="empty-state">
            <i data-feather="shopping-bag"></i>
            <h3>Your wardrobe is empty</h3>
            <p>Start by adding your first clothing item</p>
            <button 
              className="btn-primary"
              onClick={() => setShowAddModal(true)}
            >
              Add Your First Item
            </button>
          </div>
        ) : (
          <div className="products-grid">
            {filteredItems.map(item => (
              <WardrobeItemCard
                key={item.id}
                item={item}
                onDelete={handleDeleteItem}
                onEdit={handleEditItem}
                onGetOutfits={handleGetOutfits}
              />
            ))}
          </div>
        )}

        {(showAddModal || editingItem) && (
          <div className="modal-overlay" onClick={handleCloseModal}>
            <div className="modal-content" onClick={(e) => e.stopPropagation()}>
              <div className="modal-header">
                <h2>{editingItem ? 'Edit Item' : 'Add New Item'}</h2>
                <button className="btn-icon" onClick={handleCloseModal}>
                  <i data-feather="x"></i>
                </button>
              </div>

              <form onSubmit={editingItem ? handleUpdateItem : handleAddItem}>
                <div className="form-grid">
                  <div className="form-group">
                    <label>Item Name *</label>
                    <input
                      type="text"
                      value={formData.name}
                      onChange={(e) => setFormData({...formData, name: e.target.value})}
                      required
                      placeholder="e.g., Blue Denim Jacket"
                    />
                  </div>

                  <div className="form-group">
                    <label>Category *</label>
                    <select
                      value={formData.category}
                      onChange={(e) => setFormData({...formData, category: e.target.value})}
                      required
                    >
                      {categories.map(cat => (
                        <option key={cat} value={cat}>
                          {cat.charAt(0).toUpperCase() + cat.slice(1)}
                        </option>
                      ))}
                    </select>
                  </div>

                  <div className="form-group">
                    <label>Brand</label>
                    <input
                      type="text"
                      value={formData.brand}
                      onChange={(e) => setFormData({...formData, brand: e.target.value})}
                      placeholder="e.g., Levi's"
                    />
                  </div>

                  <div className="form-group">
                    <label>Color</label>
                    <input
                      type="text"
                      value={formData.color}
                      onChange={(e) => setFormData({...formData, color: e.target.value})}
                      placeholder="e.g., Navy Blue"
                    />
                  </div>

                  <div className="form-group">
                    <label>Color Code</label>
                    <input
                      type="color"
                      value={formData.colorHex}
                      onChange={(e) => setFormData({...formData, colorHex: e.target.value})}
                    />
                  </div>

                  <div className="form-group">
                    <label>Size</label>
                    <input
                      type="text"
                      value={formData.size}
                      onChange={(e) => setFormData({...formData, size: e.target.value})}
                      placeholder="e.g., M, L, XL"
                    />
                  </div>

                  <div className="form-group">
                    <label>Material</label>
                    <input
                      type="text"
                      value={formData.material}
                      onChange={(e) => setFormData({...formData, material: e.target.value})}
                      placeholder="e.g., Cotton, Denim"
                    />
                  </div>

                  <div className="form-group">
                    <label>Tags</label>
                    <input
                      type="text"
                      value={formData.tags}
                      onChange={(e) => setFormData({...formData, tags: e.target.value})}
                      placeholder="e.g., casual, summer"
                    />
                  </div>

                  <div className="form-group full-width">
                    <label>Image URL</label>
                    <input
                      type="url"
                      value={formData.imageUrl}
                      onChange={(e) => setFormData({...formData, imageUrl: e.target.value})}
                      placeholder="https://example.com/image.jpg"
                    />
                  </div>

                  <div className="form-group full-width">
                    <label>Description</label>
                    <textarea
                      value={formData.description}
                      onChange={(e) => setFormData({...formData, description: e.target.value})}
                      placeholder="Add any additional notes..."
                      rows="3"
                    />
                  </div>
                </div>

                <div className="modal-actions">
                  <button type="button" className="btn-secondary" onClick={handleCloseModal}>
                    Cancel
                  </button>
                  <button type="submit" className="btn-primary">
                    {editingItem ? 'Update Item' : 'Add Item'}
                  </button>
                </div>
              </form>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default WardrobePage;

