import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';

const ProductDetailPage = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchProduct();
  }, [id]);

  const fetchProduct = async () => {
    try {
      setLoading(true);
      setError(null);
      const response = await axios.get(`/api/products/${id}`);
      setProduct(response.data);
    } catch (err) {
      console.error('Error fetching product:', err);
      setError(err.response?.data?.message || 'Product not found');
      setProduct(null);
    } finally {
      setLoading(false);
    }
  };

  const handleGetRecommendations = () => {
    navigate(`/recommendations/${id}`);
  };

  const handleBackToSearch = () => {
    navigate(-1);
  };

  if (loading) {
    return (
      <div className="product-detail-page">
        <div className="container">
          <div className="loading-state">
            <div className="loading-spinner"></div>
            <p>Loading product details...</p>
          </div>
        </div>
      </div>
    );
  }

  if (error || !product) {
    return (
      <div className="product-detail-page">
        <div className="container">
          <div className="error-state">
            <i data-feather="alert-circle"></i>
            <h2>Product Not Found</h2>
            <p>{error || 'The requested product could not be found.'}</p>
            <button 
              className="btn-primary" 
              onClick={handleBackToSearch}
            >
              Back to Search
            </button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="product-detail-page">
      <div className="container">
        {/* Breadcrumb */}
        <nav className="breadcrumb">
          <button 
            className="breadcrumb-link"
            onClick={handleBackToSearch}
          >
            <i data-feather="arrow-left"></i>
            Back to Search
          </button>
        </nav>

        <div className="product-detail">
          {/* Product Image */}
          <div className="product-image-section">
            <div className="product-image">
              {product.imageUrl ? (
                <img 
                  src={product.imageUrl} 
                  alt={product.name}
                  onError={(e) => {
                    e.target.style.display = 'none';
                    e.target.nextSibling.style.display = 'flex';
                  }}
                />
              ) : null}
              <div className="image-placeholder" style={{ display: product.imageUrl ? 'none' : 'flex' }}>
                <i data-feather="image"></i>
                <span>No Image Available</span>
              </div>
            </div>
          </div>

          {/* Product Info */}
          <div className="product-info-section">
            <div className="product-header">
              <h1 className="product-title">{product.name}</h1>
              <div className="product-category">
                <span className="category-badge">{product.category}</span>
              </div>
            </div>

            <div className="product-details">
              <div className="detail-group">
                <h3>Product Information</h3>
                <div className="detail-grid">
                  {product.brand && (
                    <div className="detail-item">
                      <span className="detail-label">Brand:</span>
                      <span className="detail-value">{product.brand}</span>
                    </div>
                  )}

                  {product.color && (
                    <div className="detail-item">
                      <span className="detail-label">Color:</span>
                      <span className="detail-value">
                        {product.color}
                        {product.colorHex && (
                          <span 
                            className="color-swatch"
                            style={{ backgroundColor: product.colorHex }}
                          ></span>
                        )}
                      </span>
                    </div>
                  )}

                  {product.price && (
                    <div className="detail-item">
                      <span className="detail-label">Price:</span>
                      <span className="detail-value price">${product.price}</span>
                    </div>
                  )}

                  {product.occasion && (
                    <div className="detail-item">
                      <span className="detail-label">Occasion:</span>
                      <span className="detail-value">{product.occasion}</span>
                    </div>
                  )}

                  {product.patternType && (
                    <div className="detail-item">
                      <span className="detail-label">Pattern:</span>
                      <span className="detail-value">{product.patternType}</span>
                    </div>
                  )}
                </div>
              </div>

              {product.description && (
                <div className="detail-group">
                  <h3>Description</h3>
                  <p className="product-description">{product.description}</p>
                </div>
              )}
            </div>

            {/* Action Buttons */}
            <div className="product-actions">
              <button 
                className="btn-primary btn-large"
                onClick={handleGetRecommendations}
              >
                <i data-feather="zap"></i>
                Get AI Outfit Recommendations
              </button>

              <button className="btn-secondary">
                <i data-feather="heart"></i>
                Save to Favorites
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProductDetailPage;

src/components/SearchBar.js.content:
import React, { useState } from 'react';

const SearchBar = ({ onSearch, initialValue = '', placeholder = 'Search...' }) => {
  const [searchTerm, setSearchTerm] = useState(initialValue);
  const [isExpanded, setIsExpanded] = useState(false);
  const [quickFilters, setQuickFilters] = useState({
    color: '',
    brand: '',
    occasion: ''
  });

  const handleSubmit = (e) => {
    e.preventDefault();
    onSearch(searchTerm, quickFilters);
  };

  const handleQuickFilterChange = (filterType, value) => {
    const newFilters = { ...quickFilters, [filterType]: value };
    setQuickFilters(newFilters);
    onSearch(searchTerm, newFilters);
  };

  const clearFilters = () => {
    const clearedFilters = { color: '', brand: '', occasion: '' };
    setQuickFilters(clearedFilters);
    onSearch(searchTerm, clearedFilters);
  };

  const hasActiveFilters = Object.values(quickFilters).some(filter => filter);

  return (
    <div className="search-bar-container">
      <form className="search-bar" onSubmit={handleSubmit}>
        <div className="search-input-wrapper">
          <i data-feather="search" className="search-icon"></i>
          <input
            type="text"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            placeholder={placeholder}
            className="search-input"
          />
          <button 
            type="button"
            className="filter-toggle"
            onClick={() => setIsExpanded(!isExpanded)}
            aria-label="Toggle filters"
          >
            <i data-feather="sliders"></i>
          </button>
        </div>

        <button type="submit" className="search-button">
          Search
        </button>
      </form>

      {/* Quick Filters */}
      {isExpanded && (
        <div className="quick-filters">
          <div className="quick-filters-header">
            <span>Quick Filters</span>
            {hasActiveFilters && (
              <button 
                type="button"
                className="clear-filters"
                onClick={clearFilters}
              >
                Clear All
              </button>
            )}
          </div>

          <div className="quick-filters-grid">
            <div className="filter-group">
              <label>Color</label>
              <select
                value={quickFilters.color}
                onChange={(e) => handleQuickFilterChange('color', e.target.value)}
              >
                <option value="">Any Color</option>
                <option value="black">Black</option>
                <option value="white">White</option>
                <option value="blue">Blue</option>
                <option value="red">Red</option>
                <option value="green">Green</option>
                <option value="yellow">Yellow</option>
                <option value="brown">Brown</option>
                <option value="gray">Gray</option>
                <option value="pink">Pink</option>
                <option value="purple">Purple</option>
              </select>
            </div>

            <div className="filter-group">
              <label>Brand</label>
              <input
                type="text"
                value={quickFilters.brand}
                onChange={(e) => handleQuickFilterChange('brand', e.target.value)}
                placeholder="Enter brand name"
              />
            </div>

            <div className="filter-group">
              <label>Occasion</label>
              <select
                value={quickFilters.occasion}
                onChange={(e) => handleQuickFilterChange('occasion', e.target.value)}
              >
                <option value="">Any Occasion</option>
                <option value="casual">Casual</option>
                <option value="formal">Formal</option>
                <option value="business">Business</option>
                <option value="party">Party</option>
                <option value="date">Date</option>
                <option value="workout">Workout</option>
                <option value="travel">Travel</option>
                <option value="beach">Beach</option>
              </select>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default SearchBar;

routes/api.js.content:
const express = require('express');
const router = express.Router();
const storage = require('../data/storage');

// Get personalized bundles for homepage
router.get('/bundles/personalized', async (req, res) => {
  try {
    const bundles = await storage.getPersonalizedBundles();
    res.json(bundles);
  } catch (error) {
    console.error('Error fetching personalized bundles:', error);
    res.status(500).json({ 
      error: 'Failed to fetch personalized bundles',
      message: 'Unable to load recommendations at this time'
    });
  }
});

// Search products with filters
router.get('/search', async (req, res) => {
  try {
    const { q, category, color, brand, occasion, minPrice, maxPrice } = req.query;

    const filters = {
      searchTerm: q,
      category,
      color,
      brand,
      occasion,
      minPrice: minPrice ? parseFloat(minPrice) : undefined,
      maxPrice: maxPrice ? parseFloat(maxPrice) : undefined
    };

    const results = await storage.searchProducts(filters);

    res.json({
      products: results,
      total: results.length,
      filters: filters
    });
  } catch (error) {
    console.error('Search error:', error);
    res.status(500).json({ 
      error: 'Search failed',
      message: 'Unable to perform search at this time'
    });
  }
});

// Get product by ID
router.get('/products/:id', async (req, res) => {
  try {
    const { id } = req.params;
    const product = await storage.getProductById(id);

    if (!product) {
      return res.status(404).json({ 
        error: 'Product not found',
        message: 'The requested product could not be found'
      });
    }

    res.json(product);
  } catch (error) {
    console.error('Error fetching product:', error);
    res.status(500).json({ 
      error: 'Failed to fetch product',
      message: 'Unable to load product details'
    });
  }
});

// Get AI recommendations for a product
router.get('/recommendations/:id', async (req, res) => {
  try {
    const { id } = req.params;
    const { shuffle } = req.query;

    const product = await storage.getProductById(id);
    if (!product) {
      return res.status(404).json({ 
        error: 'Product not found',
        message: 'Cannot generate recommendations for unknown product'
      });
    }

    const recommendations = await storage.getRecommendations(id, shuffle === 'true');

    res.json({
      product,
      recommendations,
      total: recommendations.length
    });
  } catch (error) {
    console.error('Error fetching recommendations:', error);
    res.status(500).json({ 
      error: 'Failed to generate recommendations',
      message: 'Unable to load outfit recommendations at this time'
    });
  }
});

// Save outfit to user's profile
router.post('/saved-outfits', async (req, res) => {
  try {
    const { outfitId } = req.body;

    if (!outfitId) {
      return res.status(400).json({ 
        error: 'Missing outfit ID',
        message: 'Outfit ID is required to save'
      });
    }

    const savedOutfit = await storage.saveOutfit(outfitId);

    res.json({
      message: 'Outfit saved successfully',
      savedOutfit
    });
  } catch (error) {
    console.error('Error saving outfit:', error);
    res.status(500).json({ 
      error: 'Failed to save outfit',
      message: 'Unable to save outfit at this time'
    });
  }
});

// Remove saved outfit
router.delete('/saved-outfits/:id', async (req, res) => {
  try {
    const { id } = req.params;

    const success = await storage.removeSavedOutfit(id);

    if (!success) {
      return res.status(404).json({ 
        error: 'Saved outfit not found',
        message: 'The outfit was not found in your saved items'
      });
    }

    res.json({
      message: 'Outfit removed successfully'
    });
  } catch (error) {
    console.error('Error removing saved outfit:', error);
    res.status(500).json({ 
      error: 'Failed to remove outfit',
      message: 'Unable to remove outfit at this time'
    });
  }
});

// Rate outfit combination
router.post('/rate-combination', async (req, res) => {
  try {
    const { outfitId, rating } = req.body;

    if (!outfitId || rating === undefined) {
      return res.status(400).json({ 
        error: 'Missing required fields',
        message: 'Outfit ID and rating are required'
      });
    }

    const ratedOutfit = await storage.rateOutfit(outfitId, rating);

    res.json({
      message: 'Rating saved successfully',
      outfit: ratedOutfit
    });
  } catch (error) {
    console.error('Error rating outfit:', error);
    res.status(500).json({ 
      error: 'Failed to save rating',
      message: 'Unable to save your rating at this time'
    });
  }
});

// Update user profile
router.post('/user-profile', async (req, res) => {
  try {
    const { physique, skinTone, styleVibe, occasion } = req.body;

    const profile = await storage.updateUserProfile({
      physique,
      skinTone,
      styleVibe,
      occasion
    });

    res.json({
      message: 'Profile updated successfully',
      profile
    });
  } catch (error) {
    console.error('Error updating profile:', error);
    res.status(500).json({ 
      error: 'Failed to update profile',
      message: 'Unable to save profile changes at this time'
    });
  }
});

// Get categories
router.get('/categories', (req, res) => {
  try {
    const categories = storage.getCategories();
    res.json(categories);
  } catch (error) {
    console.error('Error fetching categories:', error);
    res.status(500).json({ 
      error: 'Failed to fetch categories',
      message: 'Unable to load categories at this time'
    });
  }
});

// Get items by category
router.get('/items/:category', async (req, res) => {
  try {
    const { category } = req.params;
    const items = await storage.getItemsByCategory(category);

    res.json({
      category,
      items,
      total: items.length
    });
  } catch (error) {
    console.error('Error fetching items:', error);
    res.status(500).json({ 
      error: 'Failed to fetch items',
      message: 'Unable to load items for this category'
    });
  }
});

// Health check endpoint
router.get('/health', (req, res) => {
  res.json({ 
    status: 'healthy',
    timestamp: new Date().toISOString(),
    message: 'Drip Check API is running'
  });
});

router.post('/users/:userId/wardrobe', async (req, res) => {
  try {
    const { userId } = req.params;
    const itemData = req.body;

    if (!itemData.name || !itemData.category) {
      return res.status(400).json({ 
        error: 'Missing required fields',
        message: 'Item name and category are required'
      });
    }

    const item = await storage.addWardrobeItem(userId, itemData);

    res.status(201).json({
      message: 'Item added to wardrobe successfully',
      item
    });
  } catch (error) {
    console.error('Error adding wardrobe item:', error);
    res.status(500).json({ 
      error: 'Failed to add item',
      message: 'Unable to add item to wardrobe at this time'
    });
  }
});

router.get('/users/:userId/wardrobe', async (req, res) => {
  try {
    const { userId } = req.params;
    const { category, color } = req.query;

    const filters = { category, color };
    const items = await storage.getWardrobeItems(userId, filters);

    res.json({
      items,
      total: items.length
    });
  } catch (error) {
    console.error('Error fetching wardrobe items:', error);
    res.status(500).json({ 
      error: 'Failed to fetch wardrobe items',
      message: 'Unable to load wardrobe at this time'
    });
  }
});

router.patch('/users/:userId/wardrobe/:itemId', async (req, res) => {
  try {
    const { userId, itemId } = req.params;
    const itemData = req.body;

    const updatedItem = await storage.updateWardrobeItem(itemId, userId, itemData);

    if (!updatedItem) {
      return res.status(404).json({ 
        error: 'Item not found',
        message: 'The wardrobe item could not be found'
      });
    }

    res.json({
      message: 'Item updated successfully',
      item: updatedItem
    });
  } catch (error) {
    console.error('Error updating wardrobe item:', error);
    res.status(500).json({ 
      error: 'Failed to update item',
      message: 'Unable to update wardrobe item at this time'
    });
  }
});

router.delete('/users/:userId/wardrobe/:itemId', async (req, res) => {
  try {
    const { userId, itemId } = req.params;

    const success = await storage.deleteWardrobeItem(itemId, userId);

    if (!success) {
      return res.status(404).json({ 
        error: 'Item not found',
        message: 'The wardrobe item could not be found'
      });
    }

    res.status(204).send();
  } catch (error) {
    console.error('Error deleting wardrobe item:', error);
    res.status(500).json({ 
      error: 'Failed to delete item',
      message: 'Unable to delete wardrobe item at this time'
    });
  }
});

router.get('/users/:userId/wardrobe/:itemId/recommendations', async (req, res) => {
  try {
    const { userId, itemId } = req.params;

    const result = await storage.getWardrobeRecommendations(itemId, userId);

    if (!result.item) {
      return res.status(404).json({ 
        error: 'Item not found',
        message: 'Cannot generate recommendations for unknown item'
      });
    }

    res.json(result);
  } catch (error) {
    console.error('Error fetching wardrobe recommendations:', error);
    res.status(500).json({ 
      error: 'Failed to generate recommendations',
      message: 'Unable to load outfit recommendations at this time'
    });
  }
});

module.exports = router;

src/components/ProductCard.js.content:
import React from 'react';

const ProductCard = ({ product, onClick }) => {
  const handleClick = () => {
    if (onClick) {
      onClick(product.id);
